$(function(){$("input[type=file].file-input").each(function(e,t){if("undefined"==typeof $(this).attr("data-bfi-disabled")){var i="Browse";"undefined"!=typeof $(this).attr("title")&&(i=$(this).attr("title"));var p=$(t),n=$("<div>").append(p.eq(0).clone()).html();p.replaceWith('<a class="file-input-wrapper btn '+p.attr("class")+'">'+i+n+"</a>")}}).promise().done(function(){$(".file-input-wrapper").mousemove(function(e){var t,i,p,n,a,l,f,r;i=$(this),t=i.find("input"),p=i.offset().left,n=i.offset().top,a=t.width(),l=t.height(),f=e.pageX,r=e.pageY,moveInputX=f-p-a+20,moveInputY=r-n-l/2,t.css({left:moveInputX,top:moveInputY})}),$(".file-input-wrapper input[type=file]").change(function(){$(this).parent().next(".file-input-name").remove(),$(this).prop("files").length>1?$(this).parent().after('<span class="file-input-name">'+$(this)[0].files.length+" files</span>"):$(this).parent().after('<span class="file-input-name">'+$(this).val().replace("C:\\fakepath\\","")+"</span>")})});var e="<style>.file-input-wrapper { overflow: hidden; position: relative; cursor: pointer; z-index: 1; }.file-input-wrapper input[type=file], .file-input-wrapper input[type=file]:focus, .file-input-wrapper input[type=file]:hover { position: absolute; top: 0; left: 0; cursor: pointer; opacity: 0; filter: alpha(opacity=0); z-index: 99; outline: 0; }.file-input-name { margin-left: 8px; }</style>";$("link[rel=stylesheet]").eq(0).before(e)});